import React from 'react';
import './FruitDes.css';

const FruitDesc = props => {
    return (
        <div class="detail-container">
            <h2>Welcome to the angular store</h2>
            <h2>please select the products you want and add them in your shopping cart</h2>
            <h2>When your are done , click shopping cart icon to review your order and checkout</h2>
        </div>
    )
};

export default FruitDesc;
