import React from 'react';
import './AvailableFruit.css';
import './AddToCartButton.css';
import AddToCartButton from '../../components/Cart/AddToCartButton';
import apple from '../../assets/apple.png';
//import mango from '../../assets/mango.png';
//import grapes from '../../assets/grapes.png';
//import pineapple from '../../assets/pineapple.png';
//import orange from '../../assets/orange.png';


const DUMMY_FRUITS = [
    {
        id: 'F1',
        name: 'Apple',
        description: 'Eat one apple every day to keep the doctor away',
        price: '$12',
    },
    {
        id: "F2",
        name: "Grapes",
        description: "Apple is great, but grapes are even better",
        price: '$18',
    },
    {
        id: "F3",
        name: "Mango",
        description: "The king of all fruit",
        price: '$10',
    },
    {
        id: "F4",
        name: "PineApple",
        description: "No pine, no apple, just a whole lot of pineapple.",
        price: '$10',
    },
    {
        id: "F5",
        name: "Orange",
        description: "Orange fruit is always good.",
        price: '$12',
    },
]


//const FruitList = DUMMY_FRUITS.map();

const AvailableFruit = props => {
    return (
        <div class="products-container">
            <div class="products-list">
                <ul>
                <li class="product-item">
								<span>
									<img class="fruit-image" src={apple} alt="" srcset="" />
								</span>
								<span class="product-list-detail">
									<h1>Apple</h1>
									<h2>Eat one apple every day to keep the doctor away</h2>
								</span>
								<span>$12</span>

                                <AddToCartButton/>
							</li>
                </ul>
            </div>
        </div>
    )
};

export default AvailableFruit;
